mapper.select:
SELECT sidocd ,(select etc1 from comm_cd where cd=sidocd and grp_cd='300' ) sidoetc ,mcls ,scls ,fppcd ,fpp ,acdcacd ,round(SUM(PAYIDM)/1000,0) payidm ,SUM(PAYMGAR) paymgar ,SUM(CNCNT) fmcnt ,round((SUM(PAYIDM)/SUM(CNCNT))/1000 ,0) avgidm FROM ( SELECT sidocd ,(SELECT CD_NM FROM COMM_CD WHERE CD=FRC_MCLS AND GRP_CD='110') mcls ,(SELECT CD_NM FROM COMM_CD WHERE CD=FRC_SCLS AND GRP_CD='120') scls ,FPPCD, (SELECT CD_NM FROM COMM_CD WHERE CD=FPPCD AND GRP_CD='100') fpp ,ACDCACD ,PAYIDM ,PAYMGAR , CNCNT FROM ( SELECT sidocd ,FRC_MCLS ,FRC_SCLS ,FPPCD , SUM(PAYIDM) PAYIDM , SUM(PAYMGAR) PAYMGAR ,ACDCACD, CNCNO, COUNT(1) CNCNT FROM FDI_OBJPAY WHERE 1=1
AND sidocd = #{sidocd}
AND frc_mcls =#{mcls}
AND frc_scls =#{scls}
AND fppcd = #{fppcd}
GROUP BY sidocd, FRC_MCLS, FRC_SCLS, FPPCD, ACDCACD,CNCNO ORDER BY sidocd, FRC_MCLS, FRC_SCLS, FPPCD ) ) GROUP BY sidocd, mcls, scls, fppcd, fpp, acdcacd ORDER BY sidocd, mcls, scls, fppcd, acdcacd
